import React from 'react';
import { withStyles } from "@material-ui/core/styles";

import Button from '@material-ui/core/Button';


const styles = theme => ({
  root: {
    backgroundColor: "red"
  },
  btnc: {
    borderStyle: 'dashed',
    borderColor: 'red',

  }
});

class UploadButton extends React.Component {

    constructor(props){

        super(props);
        this.handleChange = this.handleChange.bind(this);
    }


    handleChange (event) {
            event.click();

            const self = this;

            event.onchange = function () {
                const image = event.files[0];
                const reader = new FileReader();
                reader.onload = function () {
                    const base64 = reader.result;
                    };

                this.props.onUpload(reader.readAsDataURL(image));
                event.onchange = null;
                event.remove();
                };
            }

    render() {
      const { classes } = this.props;
        return (
            <div  >
              <input className={classes.buttonHidden}
                accept="image/*"
                id="contained-button-file"
                onChange={this.handleChange}
                type="file"
              />
              <label className={classes.button1} htmlFor="contained-button-file">
                <Button variant="contained" color="primary" component="span">
                  Add an image
                </Button>
              </label>
            </div>
          );
    }

}
export default withStyles(styles, { withTheme: true })(UploadButton);
